#Comando para criar máquinas virtuais do laboratório 1 de cluster#Defina na variável a baixo o nome da VM$nome = Read-Host$vhd01 = "D:\Hyper-V\VHD\SRV2016.vhdx"$DiscoPai = "D:\Hyper-V\VHD_PAI\Win2016Pai.vhdx"New-VHD -Path $vhd01 -ParentPath $DiscoPai -DifferencingNew-VM -Name "SRV2016 $nome" -MemoryStartupBytes 2GB -Path "D:\Hyper-V\MAQUINAS" -Generation 2 -VHDPath $vhd01